definition.script:
@Library('github.com/iocanel/syndesis-pipeline-library@release-improvements')
def parameters = [:]


slave {
    withArbitraryUser () {
        withMaven (mavenSettingsXmlSecret: 'm2-settings', mavenLocalRepositoryPath: '/home/jenkins/mvnrepo/', serviceAccount: 'jenkins') {
            withSshKeys {
                withGpgKeys (image: 'centos:centos7') {
                    inside {
                        git 'git@github.com:atlasmap/atlasmap.git'
                        container('maven') {
                            sh 'git config --global user.email "rhipaasuser@gmail.com"'
                            sh 'git config --global user.name "Syndesis CI"'
                            def releasedVersion = sonatypeRelease(releaseVersion: "${releaseVersion}", developmentVersion: "${developmentVersion}", mavenOpts: " -Pci,release")
                            imagePush(name: 'syndesis-atlasmap-config', repo: 'syndesis-atlasmap', imageStreamTag: "syndesis-atlasmap:latest")
                            imagePush(name: 'syndesis-atlasmap-config', repo: 'syndesis-atlasmap', imageStreamTag: "syndesis-atlasmap:1.0")
                            imagePush(name: 'syndesis-atlasmap-config', repo: 'syndesis-atlasmap', imageStreamTag: "syndesis-atlasmap:${releasedVersion}")
                        }
                    }
                }
            }
        }
    }
}
