definition.script:
@Library('github.com/syndesisio/syndesis-pipeline-library')
def id = buildId()

slave {
    withOpenshift {
        withMaven(mavenImage: '172.30.39.149:5000/syndesis-ci/maven-with-repo:latest', mavenSettingsXmlSecret: 'm2-settings', serviceAccount: 'jenkins') {
            inside {
                git 'https://github.com/syndesisio/syndesis'
                shareBinary('openshift', 'oc')
                    container('maven') {
                     sh """
                     export PATH=\${PATH}:\${HOME}/bin
                     git submodule update --init --recursive

                     cd connectors
                     mvn clean install -DskipTests -B -Dmaven.repo.local=/home/jenkins/.m2/repository -Duser.home=/home/jenkins
                     cd ..

                     cd integration-runtime
                     mvn clean install -DskipTests -B -Dmaven.repo.local=/home/jenkins/.m2/repository -Duser.home=/home/jenkins
                     cd ..

                     cd syndesis-verifier
                     mvn clean install -DskipTests -B -Dmaven.repo.local=/home/jenkins/.m2/repository -Duser.home=/home/jenkins
                     cd ..

                     cd rest
                     mvn clean install -DskipTests -B -Dmaven.repo.local=/home/jenkins/.m2/repository -Duser.home=/home/jenkins
                     cd ..

                     cp -rf /usr/local/share/maven/repository/* /home/jenkins/.m2/repository/
                     cd /home/jenkins/.m2/repository/
                     tar -cvf /home/jenkins/repository.tar .
                     oc start-build -F --from-archive=/home/jenkins/repository.tar maven-with-repo
                     """
                    }
                }
            }
        }
}
