definition.script:
@Library('github.com/iocanel/syndesis-pipeline-library@misc')
def parameters = [:]


slave {
    withArbitraryUser () {
        withMaven (mavenSettingsXmlSecret: 'm2-settings', mavenLocalRepositoryPath: '/home/jenkins/mvnrepo/') {
            withSshKeys {
                withGpgKeys (image: 'centos:centos7') {
                    inside {
                        git 'git@github.com:syndesisio/syndesis-rest.git'
                        container('maven') {
                            sh 'git config --global user.email "rhipaasuser@gmail.com"'
                            sh 'git config --global user.name "Syndesis CI"'
                            def releasedVersion = sonatypeRelease(releaseVersion: "${releaseVersion}", developmentVersion: "${developmentVersion}", mavenOpts: " -Pci,release")
                            imagePush(name: 'syndesis-rest-config', repo: 'syndesis-rest', imageStreamTag: 'syndesis-rest:latest')
                            imagePush(name: 'syndesis-rest-config', repo: 'syndesis-rest', imageStreamTag: 'syndesis-rest:1.0')
                            imagePush(name: 'syndesis-rest-config', repo: 'syndesis-rest', imageStreamTag: 'syndesis-rest:${releasedVersion}')
                        }
                    }
                }
            }
        }
    }
}
